import './App.css';
import { useState } from "react";

const Checkboxes = ({addIningridients, checkedIngridients, setSelectedIngriedients, selectedIngridients}) => {

  const [checkedState, setCheckedState] = useState(
    new Array(ingridients.length).fill(false)
  );

  const handleOnChange = (position) => {
    const updatedCheckedState = checkedState.map((item, index) =>
      index === position ? !item : item
    );
    setCheckedState(updatedCheckedState);
    let newList = []
    for (let i=0; i<ingridients.length; i++){
      if (checkedState[i] === true){
        newList = newList.concat(ingridients[i])

      } 
    }
    //setSelectedIngriedients(newList)
  }
  console.log(checkedIngridients)

    const getCheckList = (checkedIngridients) => {
      if (checkedIngridients === undefined) {
        let lst = []
        for (let i=0; i<ingridients.length; i++){
          if (checkedState[i] === true){
            console.log(ingridients[i], checkedIngridients)
            lst = lst.concat(ingridients[i])

          } 
        }
        return lst
      }
      else{
        let lst= checkedIngridients
        for (let i=0; i<ingridients.length; i++){
          if (checkedState[i] === true){
            console.log(ingridients[i], checkedIngridients)
            lst = lst.concat(lst[i])

          } 
        }
        return lst
      }



    }
    checkedIngridients =  getCheckList()
    setSelectedIngriedients(checkedIngridients)


    console.log(checkedState, checkedIngridients)

  ;

  return (
    <div>
      <h3>Select Toppings</h3>
      <ul>
        {ingridients.map((item, index) => {
          return (
            <li key={index}>
              <div>
                <div>
                  <input
                    type="checkbox"
                    id={`custom-checkbox-${index}`}
                    name={item}
                    value={item}
                    checked={checkedState[index]}
                    onChange={() => handleOnChange(index)}
                  />
                  <label htmlFor={`custom-checkbox-${index}`}>{item}</label>
                </div>

              </div>
            </li>
          );
        })}

      </ul>
    </div>
  );
}




const App = () => {

  const ingridients = ['vodka', 'gin', 'lemon']
  const [selectedIngridients, setSelectedIngriedients] = useState([]) 
  let checkedIngridients = []

  const addIngridient = (string) => {
    let newList = selectedIngridients.concat(string)
    setSelectedIngriedients(newList)
  }
  console.log(selectedIngridients)


  return (
    <div className="App">
      <Checkboxes addIngridient = {addIngridient} ingridients={ingridients} checkedIngridients={checkedIngridients} selectedIngridients= {selectedIngridients} setSelectedIngriedients={setSelectedIngriedients}/>
      <h2>Valitut raaka-aineet</h2>
      {selectedIngridients}
    </div>
  );
}

export default App;
