import ReactDOM from "react-dom";
import { BrowserRouter, Routes, Route } from "react-router-dom";
import Home from "./pages/Home";
import Layout from "./pages/Layout";
import database from './db/cocktails.json'
import react, { useState } from "react";

export default function App() {

  // saving lists from database to use in components
  const cocktails = database.cocktails
  const ingridients = database.ingridients
  console.log(ingridients)

  const [selectedIngridients, setSelectedIngridients] = useState([])


  let sublist = []
    for (const key in ingridients){
      console.log(ingridients[key])
      const ingridientsByType = ingridients[key]
      for (const key in ingridientsByType){
        console.log(ingridientsByType[key], ingridientsByType[key].index)
        sublist[ingridientsByType[key].index] = ingridientsByType[key].name

      }
      console.log(ingridients[key])

    }



  return (
    <BrowserRouter>
      <Routes>
        <Route path="/" element={<Layout />}>
          <Route index element={<Home cocktails={cocktails} ingridients = {ingridients} />} />

        </Route>
      </Routes>
    </BrowserRouter>
  );
}

ReactDOM.render(
  <App />, 
  document.getElementById('root')
)
