        for (let i in lst){
            console.log(lst[i].index)
            if (selectedIngridients[lst[i].index] === true){
                console.log('jiihaa')
                ingridientsLst.concat(lst[i])
            }
        }
        return(
            ingridientsLst
        )
    })}

    findDrinks()

    console.log()

  return(
    <div>
      <Checkboxes ingridients={ingridients} selectedIngridients={selectedIngridients} changeSelectedIngridient = {changeSelectedIngridient}/>
